import React from 'react'
import Eventcard from '../../Components/EventCard/Eventcard'
import axios from 'axios'
import { useEffect,useState } from 'react'

export default function Event() {

  const [upcEvents, setUpcEvents] = useState([])
  const [pastevents, setPastEvents] = useState([])

  useEffect(() => {

    var config = {
      method: 'get',
      url: 'http://localhost:8001/upload/getEvents',
      headers: {}
    };

    axios(config)
      .then(function (response) {
        // console.log(JSON.stringify(response.data.upcomingEvents));

        let upcEvents = []
        let pastEvents = []

        for (const key in response.data.upcomingEvents) {
          upcEvents.push({ data : response.data.upcomingEvents[key] })
          }

        setUpcEvents(upcEvents)

      })
      .catch(function (error) {
        console.log(error);
      });

  }, [])


  return (
    <>
      <section className='events-container' >

        <div className="upcomingEvents">
          <p>UPCOMING EVENTS</p>
          <div className='upc-events-container'>

            <Eventcard  />
            

          </div>
        </div>

      </section>
    </>
  )
}
